#!/bin/bash
12choose() { IFS=$'\n' ls=($(ls -1)); IFS=$'\n\t '; lsw="$(printf %b "${ls[*]}"|wc --max-line-length)"; ((${#ls[*]} < LINES)) && lsh="${#ls[*]}" || lsh="$((LINES -2))"; printf %b "${ls[*]}"|FZF_DEFAULT_OPTS= fzf --tmux "center,$((lsw * 2 + 6)),$((lsh + 1))" --ansi -m -i --cycle --header-first --color "bg:126,fg:7,gutter:66,bg+:9,preview-bg:232,alt-bg:235,gap-line:6,info:6,selected-bg:88,selected-hl:66,hl:28,ghost:240,input:9,prompt:212,input-bg:9,list-bg:24" --margin 0 --ghost "${PWD/*\//}" --highlight-line --no-separator --border none --info inline --preview 'ls {}|bat -ppfljs' --preview-window "right:$lsw,border-left,wrap" --wrap-sign "" --bind 'q:abort,focus:transform-prompt:printf %b '"'\e[2;48;5;161m\e[1;30m'"'; ls -1shp {}|head -n1|sed -e s/total\ //|cut -f1 -d" "' --ellipsis ""; }; 
