alias apps_apts'=apt list --manual-installed 2>/dev/null|tail -n+2|cut -f1 -d/|fzf --color bg:232,preview-bg:234 --border top --border-label-pos top --border-label " .... " --bind "0:change-preview-window(top,60%,noborder|80%|right,66%,right),focus:transform-border-label:printf %b "\x1b[42;38;5;232;;1m {} " --preview-window "wrap,68%" --preview "apt show {} 2>/dev/null|sed -n "/Description/,/\\n/p"|sed -e "s/Description\:\ /\x1b[1;97;4m/" -e "1a\^[[0m\n"|fmt -w $((COLUMNS/10*8)) |sed -e "s/^[        ]*//g"; apt show {} 2>/dev/null|tail -n+2|bat -ppfljs --theme Nord"'
