git_ssh() { local FZF_DEFAULT_OPTS=; gggg="$(ls ~/.ssh|grep -vE '.pub|known_hosts|authorized_keys'|fzf --preview '(cat ~/.ssh/{}.pub|tr " " "\n"; echo; echo; cat ~/.ssh/{})|bat -ppflgo' -i --wrap --preview-window 'wrap' --wrap-sign '' --bind 'q:abort')"; printf %b "\n\n\e[A -- change git ssh-key to: $gggg ? [Y/n] "; read -sn1 ny; [ -z "$ny" ] && git config set core.sshCommand "ssh -i ~/.ssh/$gggg"; echo; }; 
