ssport() { $HOME/bin/ssss -t4ln|tail -n+2|column --table --table-order 5 --table-hide 3,6,4|cut -f2- -d":"|column --table --table-order 1,3,2|sort -nr > $HOME/logs/ss.log; sz=($(wc -l --max-line-length $HOME/logs/ss.log)); tmux display-popup -Eb none -w $((sz[1]+6)) -h $((sz+1)) 'printf %b "\e]11;#000812\e\\"; bat -ppflgo $HOME/logs/ss.log; printf %b "\e[42;30m-\e[222b"; bash -c "read -sn1"'; }; 
