(printf %b '<!DOCTYPE html> <html> <head> <meta charset="ISO-8859-1"> <style type="text/css"> body { margin:0;padding:0; background:#222222; width:100%; max-width:100vw; height:100%; overflow-x:hidden;overflow-y:auto; } img{margin-top:-20px;}pre{position:relative;margin-bottom:-20px;font-family:monospace;font-size:8pt;z-index:2;font-weight:400;text-shadow:0.5px2px1px#66442288,-1px-1px1px#00000066;}.lo{background:#555588;padding:2px;margin:2px;} .af_line { color: gray; text-decoration: none; } a:hover {transition:.2s;color:#884422;font-weight:800;border:solid 5px #ee22dd; } a { font-weight:400;transition:.2s; text-decoration:none; color:#ee4488;border:solid 5px #ee2266; } .bo{display:flex; flex-wrap:wrap; align-items:center; } </style> <title>Source file</title></head><body><div class="bo">'; for i in $(ls -1); do printf %b "<a href="$i"><div class="lo"><pre><ul><b>$(printf %b "${i:(-18)}")</b></ul><br>$(mediainfo $i|grep -E 'Width|Height|File|Duration'|sort -u|sed -e 's/File\ s/S/' -e 's/\ \ //g' -e /Source/d|column --table --separator : --output-separator :)</pre><img src="/mm/$i.jpg"></a></div>\n"; done; printf %b "<br><hr><br></div></body></html>") | tee index2.html;
